    }
]


///////////////////// VIEWS OF EACH REPORT ELEMENT \\\\\\\\\\\\\\\\\\\\\
function DaysView(props) {
    return props.expanded ?
        <div>
            Expanded day expense information
        </div> :
        <div>
            Compact day expense information
        </div>
}

function MonthsView(props) {

    return props.expanded ? 
        <div>
            Expanded month expense information
        </div>: 
        <div>
            Compact month expense information
        </div>

}


class ExpenseReport extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            years: JSON.parse(JSON.stringify(years)),
            months: JSON.parse(JSON.stringify(months)),
            days: JSON.parse(JSON.stringify(days)),
        };
    }
    render() {
        return (
            <div className = "container">
                <NavBar 
                    profile = ''
                    makePlan = ''
                    settings = ''
                    expenseReport = ' active'/>

                <p>This is going to be expense report page</p>
            </div>
        );
    }
}

export default ExpenseReport;
