import React, { Component } from 'react';
import NavBar from './nav.js'

///////////////////// STYLINGS \\\\\\\\\\\\\\\\\\\\\
const containerStyle = {
    backgroundColor: 'rgba(16, 77, 8,0.2)',
    padding: 10,
    borderRadius: 30,
    margin: 10,
}

const ulStyle = {
    padding: 0,
    marginRight: 20,
}

///////////////////// DATA COMING FROM DATABASE \\\\\\\\\\\\\\\\\\\\\
const days = [
    {
        totalExpense: 10000,
        toBeSpent: 16000,
        availibleAmount: 41000,
    }, 
    {
        totalExpense: 10000,
        toBeSpent: 16000,
        availibleAmount: 41000,
    },
    {
        totalExpense: 10000,
        toBeSpent: 16000,
        availibleAmount: 41000,
    },
    {
        totalExpense: 16100,
        toBeSpent: 16000,
        availibleAmount: 41000,
    },
    {
        totalExpense: 10000,
        toBeSpent: 16000,
        availibleAmount: 41000,
    },
    {
        totalExpense: 16100,
        toBeSpent: 16000,
        availibleAmount: 41000,
    },
    {
        totalExpense: 10000,
        toBeSpent: 16000,
        availibleAmount: 41000,
    },
    {
        totalExpense: 10000,
        toBeSpent: 16000,
        availibleAmount: 41000,
    },
    {
        totalExpense: 10000,
        toBeSpent: 16000,
        availibleAmount: 41000,
    },
    {
        totalExpense: 10000,
        toBeSpent: 16000,
        availibleAmount: 41000,
    },
]

const months = [
    {
        name: 'January',
        totalExpense: 39000,
        toBeSpent: 40000,
        availableAmount: 50000,
        days: JSON.parse(JSON.stringify(days))
    },
    {
        name: 'February',
        totalExpense: 39000,
        toBeSpent: 40000,
        availableAmount: 490000,
        days: JSON.parse(JSON.stringify(days))
    },
    {
        name: 'March',
        totalExpense: 41000,
        toBeSpent: 40000,
        availableAmount: 410000,
        days: JSON.parse(JSON.stringify(days))
    },
    {
        name: 'April',
        totalExpense: 30000,
        toBeSpent: 40000,
        availableAmount: 40000,
        days: JSON.parse(JSON.stringify(days))
    },
]

const years = [
    {
        year: 2020,
        totalExpense: 391000,
        toBeSpent: 491000,
        months: JSON.parse(JSON.stringify(months))
    },
    {
        year: 2020,
        totalExpense: 391000,
        toBeSpent: 491000,
        months: JSON.parse(JSON.stringify(months))
    }
]


///////////////////// VIEWS OF EACH REPORT ELEMENT \\\\\\\\\\\\\\\\\\\\\
// function DaysView(props) {
//     let expanded = props.expanded

//     return expanded ?
//         <div>
//             Expanded day expense information
//         </div> :
//         <div>
//             Compact day expense information
//         </div>
// }

class DaysView extends Component {

    constructor(props) {
        super(props)

        this.state = {
            expanded: false,
            data: props.data,
        }
    }

    expandToggling() {
        this.setState({
            expanded: !this.state.expanded
        })
    }

    render() {

        return this.state.expanded ?
            <div 
                className = "container" 
                style ={{...containerStyle}}
                onClick = {() => this.expandToggling()} >

                <ul type = 'none' style = {ulStyle}>
                    {this.state.data.days.map((day) => 
                    <li style = {{margin: 5}}>
                        finally days expanded
                    </li> ) }
                </ul>

            </div> :
            <div 
                className = "container" 
                style ={{...containerStyle}}
                onClick = {() => this.expandToggling()} >
                Compact month expense information
            </div>
    }

}

class MonthsView extends Component {

    constructor(props) {
        super(props)

        this.state = {
            expanded: false,
            data: props.data,
        }
    }

    expandToggling() {
        this.setState({
            expanded: !this.state.expanded
        })
    }

    render() {

        return this.state.expanded ?
            <div 
                className = "container" 
                style ={{...containerStyle}}
                onClick = {() => this.expandToggling()} >

                <ul type = 'none'>
                    {this.state.data.days.map((day) => 
                    <DaysView
                        data = {day} /> ) }
                </ul>

            </div> :
            <div 
                className = "container" 
                style ={{...containerStyle}}
                onClick = {() => this.expandToggling()} >
                Compact month expense information
            </div>
    }

}

class YearsView extends Component {

    constructor(props) {
        super(props)

        this.state = {
            expanded: false,
            data: props.data,
        }
    }

    expandToggling() {
        this.setState({
            expanded: !this.state.expanded
        })
    }

    render() {

        return this.state.expanded ?
            <div 
                className = "container" 
                style ={{...containerStyle}} >

                <ul type = 'none' style = {ulStyle}>
                    {this.state.data.months.map((month) => 
                    <MonthsView
                        data = {month} /> ) }
                </ul>

            </div> :
            <div 
                className = "container" 
                style ={{...containerStyle}}
                onClick = {() => this.expandToggling()} >
                Compact year expense information
            </div>
    }

}


class ExpenseReport extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            years: JSON.parse(JSON.stringify(years)),
        };
    }


    render() {

        const years = this.state.years

        const yearsViewList = years.map((year) => {
            return <li key={years.indexOf(year)} style={{margin: 5}}>
                <YearsView 
                    expanded = {false}
                    data = {year} />
            </li>
        })

        return (
            <div className = "container">
                <NavBar 
                    profile = ''
                    makePlan = ''
                    settings = ''
                    expenseReport = ' active'/>

                <ul type="none" style = {ulStyle}>
                    {yearsViewList}
                </ul>

            </div>
        );
    }
}

export default ExpenseReport;
