import NavBar from './nav.js'
import React from 'react'



// Status Colors 
const statusColors = {
    1: 'rgb(16, 77, 8,0.5)',
    2: 'rgb(112, 185, 43,0.5)',
    3: 'rgb(219, 146, 63,0.5)',
    4: 'rgb(102, 27, 9,0.5)',
}


let userImgStyle = {
    height: 100,
    width: 100,
    borderRadius: 90,
}


function UserImg(props) {
    return <img src = {props.picture} style={userImgStyle} />
}

class SingleInput extends React.Component {

    defaultValue = this.props.inputProps.defaultValue ? this.props.inputProps.defaultValue : ''

    constructor(props) {
      super(props);
      this.state = {value: defaultValue};

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
      this.setState({value: event.target.value});
    }

    handleSubmit(event) {
      this.props.onSubmit(this.state.value)
      event.preventDefault();
    }

    render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <input 
                type={this.props.inputType} 
                value={this.state.value} 
                onChange={this.handleChange} 
                placeholder={this.props.inputLabel}/>

          <input 
            type="submit" 
            value={this.props.inputSubmitLabel} />
        </form>
      );
    }
}


function UserJumbotron(props) {

    return <div className = "jumbotron jumbotron-fluid" style={{backgroundColor: statusColors[props.statusColor]}}>
        <div className = "container">
            <h1 className="display-4" >
                <UserImg picture = {props.userData.picture}/>
                {props.userData.userName}
            </h1>
            <p className="lead">You have spent {props.userData.todayExpense} out of {props.userData.income/30}</p>
        </div>
    </div>
}


export default class UserProfile extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            userName: 'Sagar Sikchi',
            picture: 'https://www.jbldrains.com/wp-content/uploads/2015/02/person-icon.png',
            hasPlan: true,
            income: 420000,
            availableAmount: 20000,
            plan: {
                amount: 30000,
                duration: 3,
            },
            isNewMonth: true,
            isIncomeRegular: true,
            todayExpense: 0,
        }
    }

    onSalaryReceiving(salaryAmount) {
        this.setState({
            isNewMonth: false,
            availableAmount: this.state.availableAmount + parseInt(salaryAmount),
        })
    }

    getStatus() {

        const everydayEx = this.state.income/30
        const todayEx = this.state.todayExpense

        if(todayEx/everydayEx < 0.5) {
            return '1'
        } else if (todayEx/everydayEx < 0.8) {
            return '2'
        } else if(todayEx/everydayEx < 1) {
            return '3'
        } 

        return '4'
    }

    onSpend(amount) {

        const prevEx = this.state.todayExpense

        this.setState({
            todayExpense: prevEx + parseInt(amount)
        })
    }

    render() {
        return <div>
            <NavBar />
            <UserJumbotron userData={this.state} statusColor = {this.getStatus()}/>
            <SingleInput 
                onSubmit = {(amount) => this.onSpend(amount)} 
                inputType = 'number'
                inputLabel = 'Your Expense Here'
                inputSubmitLabel = "Spend"

                />

            <SingleInput 
                onSubmit = {(salary) => this.onSalaryReceiving(salary)}
                inputType = 'number'
                inputSubmitLabel = 'Recieved My Salary'
                defaultValue = {40000} />

            {this.state.hasPlan ? <p> You have plan</p> : <p>You don't have a plan</p>}
        </div>
    }

}




// 7. Profile Page
// 	i. Name 
