import React, { Component } from 'react';
import NavBar from './nav.js'

const containerStyle = {
    backgroundColor: 'rgba(16, 77, 8,0.2)',
    padding: 30,
    borderRadius: 30,
    margin: 10,
    marginLeft: 20,
    marginRight: 20,
}

class MakePlan extends Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
    }

    handleSubmit(event) {

        console.log("Submitted")

        event.preventDefault()
    }

    render() {
        return (
            <div className="container">
                <NavBar 
                    profile = ''
                    makePlan = ' active'
                    settings = ''
                    expenseReport = ''/>

                <div className = "container" style ={containerStyle}>
                    <form onSubmit={this.handleSubmit} className = "form-floating">
                        <div className = "input-group input-group-lg mb-3 form-floating">
                            <input 
                                className="form-control" 
                                id="floatingInputValue"
                                type='number'
                                name= 'amount'
                                value={this.state.name}
                                onChange = {(event) => this.handleInputChange(event)} />

                            <label 
                                htmlFor="floatingInputValue" >
                                Saving Amount:  </label>

                        </div>
                        <div className = "input-group input-group-lg mb-3 form-floating">
                            <input 
                                className="form-control" 
                                id="floatingInputValue"
                                type='text'
                                name= 'duration'
                                value={this.state.username}
                                onChange = {(event) => this.handleInputChange(event)} />
                            <label 
                                htmlFor="floatingInputValue" >
                                Username:  </label>

                        </div>

                    </form>
                </div>
            </div>
        );
    }
}

export default MakePlan;
