import NavBar from './nav.js'
import React from 'react'



{/* <div class="jumbotron jumbotron-fluid">
  <div class="container">
    <h1 class="display-4">Fluid jumbotron</h1>
  </div>
</div> */}


let userImgStyle = {
    height: 100,
    width: 100,
}

let jumbotronStyle = {
    backgroundColor: 'grey'
}

function UserImg(props) {
    return <img src = {props.picture} style={userImgStyle} />
}


function UserJumbotron(props) {
    return <div className = "jumbotron jumbotron-fluid">
        <div className = "container">
            <h1 className="display-4" >
                <UserImg picture = {props.userData.picture}/>
                {props.userData.userName}
            </h1>
            <p class="lead">This is a modified jumbotron that occupies the entire horizontal space of its parent.</p>
        </div>
    </div>
}


export default class UserProfile extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            userName: 'Sagar Sikchi',
            picture: 'https://www.jbldrains.com/wp-content/uploads/2015/02/person-icon.png',
            hasPlan: true,
            income: 40000,
            plan: {
                amount: 30000,
                duration: 3,
            },
            isNewMonth: true,
            isIncomeRegular: true,
            todayExpense: [200, 10, 45, 315]
        }
    }

    render() {
        return <div>
            <NavBar />
            <UserJumbotron userData={this.state} />
        </div>
    }

}




