import React, { Component} from 'react'
import NavBar from './nav.js'


export default class Settings extends Component {
    constructor(props) {
        super(props)

        this.state = {}
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
    }

    handleSubmit(event) {

        console.log("Submitted")

        event.preventDefault()
    }

    render() {
        return <div className = "container form-style">

            <NavBar logIn = ' active' />

            <form onSubmit={this.handleSubmit} className = "form-floating component-container">

                <div className = "input-group input-group-lg mb-3 form-floating">
                    <input 
                        className="form-control" 
                        id="floatingInputValue"
                        type='text'
                        name= 'username'
                        value={this.state.username}
                        onChange = {(event) => this.handleInputChange(event)} />

                    <label 
                        htmlFor="floatingInputValue" >
                        Username/Email:  </label>

                </div>
                <div className = "input-group input-group-lg mb-3 form-floating">
                    <input 
                        className="form-control" 
                        id="floatingInputValue"
                        type='password'
                        name= 'password'
                        value={this.state.password}
                        onChange = {(event) => this.handleInputChange(event)} />

                    <label 
                        htmlFor="floatingInputValue" >
                        Password:  </label>

                </div>
                <button type="submit" className="btn btn-primary mt-3">Submit</button>

            </form>

        </div>
    }
}
