import React, { Component } from 'react';
import NavBar from './nav.js'

const containerStyle = {
    backgroundColor: 'rgba(16, 77, 8,0.2)',
    padding: 30,
    borderRadius: 30,
    margin: 10,
    marginLeft: 20,
    marginRight: 20,
}

function InvalidFeedBack(props) {
    return <div id="validationServerUsernameFeedback" class="invalid-feedback">
        {props.message}
    </div>
}

class MakePlan extends Component {
    constructor(props) {
        super(props);
        this.state = {
            planStatusStyle: {...containerStyle},
            isPlanValid: true,
            income: 40000,
            noAmount: false,
        };
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        if(name === 'duration') {
            const savingAmount = this.state.amount ? this.state.amount : null;
            const incomeVsSaving = savingAmount ? (this.state.income - savingAmount/value): null;

            if(incomeVsSaving) {
                if (incomeVsSaving >= 1000) {
                    this.setState({ isPlanValid: false })
                }
            } else {
                this.setState({ noAmount: true })
            }
        }

        this.setState({
            [name]: value
        });
    }

    handleSubmit(event) {

        console.log("Submitted")

        event.preventDefault()
    }

    render() {
        return (
            <div className="container">
                <NavBar 
                    profile = ''
                    makePlan = ' active'
                    settings = ''
                    expenseReport = ''/>

                <form onSubmit={this.handleSubmit} className = "form-floating" style ={containerStyle}>
                    <div className = "input-group input-group-lg mb-3 form-floating">
                        <input 
                            className="form-control" 
                            id="floatingInputValue"
                            type='number'
                            name= 'amount'
                            value={this.state.name}
                            onChange = {(event) => this.handleInputChange(event)} />

                        <label 
                            htmlFor="floatingInputValue" >
                            Saving Amount:  </label>

                        {this.state.noAmount ? <InvalidFeedBack  
                            message = "Above field cannot be empty"/>: null }

                    </div>
                    <div className = "input-group input-group-lg mb-3 form-floating">
                        <input 
                            className="form-control" 
                            id="floatingInputValue"
                            type='number'
                            name= 'duration'
                            value={this.state.username}
                            onChange = {(event) => this.handleInputChange(event)} />
                        <label 
                            htmlFor="floatingInputValue" >
                            Duration in Months  </label>

                        {!this.state.isPlanValid && <InvalidFeedBack 
                            message = 'You Cannot save more than your income in a month' />}

                    </div>

                    <button type="submit" className="btn btn-primary mt-3" disabled = {this.state.isPlanValid}>Submit</button>

                </form>            
            </div>
        );
    }
}

export default MakePlan;
