import NavBar from './nav.js'
import React from 'react'



// Status Colors 
const statusColors = {
    1: 'rgb(16, 77, 8,0.5)',
    2: 'rgb(112, 185, 43,0.5)',
    3: 'rgb(219, 146, 63,0.5)',
    4: 'rgb(102, 27, 9,0.5)',
}


let userImgStyle = {
    height: 100,
    width: 100,
}


function UserImg(props) {
    return <img src = {props.picture} style={userImgStyle} />
}


function UserJumbotron(props) {
    return <div className = "jumbotron jumbotron-fluid" style={{backgroundColor: statusColors[props.statusColor]}}>
        <div className = "container">
            <h1 className="display-4" >
                <UserImg picture = {props.userData.picture}/>
                {props.userData.userName}
            </h1>
            <p class="lead"></p>
        </div>
    </div>
}


export default class UserProfile extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            userName: 'Sagar Sikchi',
            picture: 'https://www.jbldrains.com/wp-content/uploads/2015/02/person-icon.png',
            hasPlan: true,
            income: 40000,
            plan: {
                amount: 30000,
                duration: 3,
            },
            isNewMonth: true,
            isIncomeRegular: true,
            todayExpense: [200, 10, 45, 315]
        }
    }

    render() {
        return <div>
            <NavBar />
            <UserJumbotron userData={this.state} />
        </div>
    }

}




// 7. Profile Page
// 	i. Name 
