import React, { Component } from 'react';
import NavBar from './nav.js'

///////////////////// STYLINGS \\\\\\\\\\\\\\\\\\\\\
const containerStyle = {
    backgroundColor: 'rgba(16, 77, 8,0.2)',
    padding: 10,
    borderRadius: 30,
    margin: 10,
}

const ulStyle = {
    padding: 0,
    marginRight: 20,
}

const collapseButtonStyle = {
    paddingTop: 1,
    paddingBottom: 1,
    marginLeft: 0,
    marginRight: 0,
    marginBottom: 0,
    borderBottomWidth: 0,
    borderLeftWidth: 0,
    borderRightWidth: 0,
    borderRadius: 0,
}

const statusExpandedColors = {
    1: 'rgba(16, 77, 8,0.2)',
    2: 'rgba(112, 185, 43,0.2)',
    3: 'rgba(219, 146, 63,0.2)',
    4: 'rgba(102, 27, 9,0.2)',
}

const statusColors = {
    1: 'rgb(16, 77, 8)',
    2: 'rgb(112, 185, 43)',
    3: 'rgb(219, 146, 63)',
    4: 'rgb(102, 27, 9)',
}

///////////////////// DATA COMING FROM DATABASE \\\\\\\\\\\\\\\\\\\\\
const days = [
    {
        totalExpense: 10000,
        toBeSpent: 16000,
        availibleAmount: 41000,
    }, 
    {
        totalExpense: 10000,
        toBeSpent: 16000,
        availibleAmount: 41000,
    },
    {
        totalExpense: 10000,
        toBeSpent: 16000,
        availibleAmount: 41000,
    },
    {
        totalExpense: 16100,
        toBeSpent: 16000,
        availibleAmount: 41000,
    },
    {
        totalExpense: 10000,
        toBeSpent: 16000,
        availibleAmount: 41000,
    },
    {
        totalExpense: 16100,
        toBeSpent: 16000,
        availibleAmount: 41000,
    },
    {
        totalExpense: 10000,
        toBeSpent: 16000,
        availibleAmount: 41000,
    },
    {
        totalExpense: 10000,
        toBeSpent: 16000,
        availibleAmount: 41000,
    },
    {
        totalExpense: 10000,
        toBeSpent: 16000,
        availibleAmount: 41000,
    },
    {
        totalExpense: 10000,
        toBeSpent: 16000,
        availibleAmount: 41000,
    },
]

const months = [
    {
        name: 'January',
        totalExpense: 39000,
        toBeSpent: 40000,
        availableAmount: 50000,
        days: JSON.parse(JSON.stringify(days))
    },
    {
        name: 'February',
        totalExpense: 39000,
        toBeSpent: 40000,
        availableAmount: 490000,
        days: JSON.parse(JSON.stringify(days))
    },
    {
        name: 'March',
        totalExpense: 41000,
        toBeSpent: 40000,
        availableAmount: 410000,
        days: JSON.parse(JSON.stringify(days))
    },
    {
        name: 'April',
        totalExpense: 30000,
        toBeSpent: 40000,
        availableAmount: 40000,
        days: JSON.parse(JSON.stringify(days))
    },
]

const years = [
    {
        year: 2020,
        totalExpense: 391000,
        toBeSpent: 491000,
        months: JSON.parse(JSON.stringify(months))
    },
    {
        year: 2021,
        totalExpense: 391000,
        toBeSpent: 491000,
        months: JSON.parse(JSON.stringify(months))
    }
]

////////////////////// UTILITY FUNCTIONS \\\\\\\\\\\\\\\\\\\\\\\\\
function getStatusColor(expectedExpense, expense, isExpanded) {
    if (!expectedExpense || !expense) return;

    let colors = isExpanded ? statusExpandedColors : statusColors

    if(expense/expectedExpense < 0.5) return colors['1'];
    if(expense/expectedExpense < 0.8) return colors['2'];
    if(expense/expectedExpense < 1) return colors['3'];
    return colors['4'];
}

function getStatusStyle(expectedExpense, expense, isExpanded) {
    let elementStyle = {...containerStyle}
    const statusColor = getStatusColor(expectedExpense, expense, isExpanded)

    elementStyle.backgroundColor = statusColor
    return elementStyle;
}


///////////////////// VIEWS OF EACH REPORT ELEMENT \\\\\\\\\\\\\\\\\\\\\

function Title(props) {

    return <h1 style = {{
        textAlign: 'center',
        fontWeight: 'bold',
        fontSize: props.fontSize,
    }}>{props.name}</h1>

}

function CollapseButton(props) {

    return <div className = "d-grid gap-2">
        <button
            onClick = {() => props.onClick()}
            style = {collapseButtonStyle}
            type="button"
            className = "btn btn-outline-success "
            >&#x39B;</button>
    </div>

}

class DaysView extends Component {

    constructor(props) {
        super(props)

        this.state = {
            expanded: false,
            data: props.data,
        }
    }

    expandToggling() {
        this.setState({
            expanded: !this.state.expanded
        })
    }

    render() {

        return this.state.expanded ?
            <div 
                className = "container" 
                style ={{...containerStyle}}
                onClick = {() => this.expandToggling()} >

                expanded days

            </div> :
            <div 
                className = "container" 
                style ={{...containerStyle}}
                onClick = {() => this.expandToggling()} >
                Compact month expense information
            </div>
    }

}

class MonthsView extends Component {

    constructor(props) {
        super(props)

        this.state = {
            expanded: false,
            data: props.data,
        }
    }

    expandToggling() {
        this.setState({
            expanded: !this.state.expanded
        })
    }

    render() {

        const expectedExpense = this.state.data.toBeSpent;
        const expense = this.state.data.totalExpense;
        const styleExpandedExpanded = getStatusStyle(expectedExpense, expense, true);
        const styleExpanded = getStatusStyle(expectedExpense, expense, false);

        return this.state.expanded ?
            <div 
                className = "container" 
                style ={style} >

                <Title 
                    name = {this.state.data.name}
                    fontSize = {25} />

                <ul type = 'none' style = {ulStyle}>
                    {this.state.data.days.map((day) => 
                    <DaysView
                        data = {day} /> ) }
                </ul>

                <CollapseButton 
                    onClick = {() => this.expandToggling()} />

            </div> :
            <div 
                className = "container" 
                style ={style}
                onClick = {() => this.expandToggling()} >
                Compact month expense information
            </div>
    }

}

class YearsView extends Component {

    constructor(props) {
        super(props)

        this.state = {
            expanded: false,
            data: props.data,
        }
    }

    expandToggling() {
        this.setState({
            expanded: !this.state.expanded
        })
    }

    render() {

        const expectedExpense = this.state.data.toBeSpent;
        const expense = this.state.data.totalExpense;
        const style = getStatusStyle(expectedExpense, expense);
        console.log(style)

        return this.state.expanded ?
            <div 
                className = "container" 
                style ={style} >

                <Title 
                    name = {this.state.data.year}
                    fontSize = {25} />

                <ul type = 'none' style = {ulStyle}>
                    {this.state.data.months.map((month) => 
                    <MonthsView
                        data = {month} /> ) }
                </ul>

                <CollapseButton onClick = {() => this.expandToggling()}/>

            </div> :
            <div 
                className = "container" 
                style ={style}
                onClick = {() => this.expandToggling()} >
                Compact year expense information
            </div>
    }

}

class ExpenseReport extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            years: JSON.parse(JSON.stringify(years)),
        };
    }


    render() {

        const years = this.state.years

        const yearsViewList = years.map((year) => {
            return <li key={years.indexOf(year)} style={{margin: 5}}>
                <YearsView 
                    expanded = {false}
                    data = {year} />
            </li>
        })

        return (
            <div className = "container">
                <NavBar 
                    profile = ''
                    makePlan = ''
                    settings = ''
                    expenseReport = ' active'/>

                <ul type="none" style = {ulStyle}>
                    {yearsViewList}
                </ul>

            </div>
        );
    }
}

export default ExpenseReport;
