import NavBar from './nav.js'
import React from 'react'



// Status Colors 
const statusColors = {
    1: 'rgb(16, 77, 8,0.5)',
    2: 'rgb(112, 185, 43,0.5)',
    3: 'rgb(219, 146, 63,0.5)',
    4: 'rgb(102, 27, 9,0.5)',
}


let userImgStyle = {
    height: 100,
    width: 100,
}


function UserImg(props) {
    return <img src = {props.picture} style={userImgStyle} />
}

function Button(props) {
    return <button onClick = {() => props.onClick()}>{props.btnVal}</button>
}


class SpendingInput extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: ''};

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
      this.setState({value: event.target.value});
    }

    handleSubmit(event) {
      alert('A name was submitted: ' + this.state.value);
      this.props.onSpend(this.state.value)
      event.preventDefault();
    }

    render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <label>
            Your Expenses Here:
            <input type="number" value={this.state.value} onChange={this.handleChange} />
          </label>
          <input type="submit" value="Confirm Expense" />
        </form>
      );
    }
}


function UserJumbotron(props) {

    return <div className = "jumbotron jumbotron-fluid" style={{backgroundColor: statusColors[props.statusColor]}}>
        <div className = "container">
            <h1 className="display-4" >
                <UserImg picture = {props.userData.picture}/>
                {props.userData.userName}
            </h1>
            <p className="lead">You have spent {props.userData.todayExpense} out of {props.userData.income/30}</p>
        </div>
    </div>
}


export default class UserProfile extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            userName: 'Sagar Sikchi',
            picture: 'https://www.jbldrains.com/wp-content/uploads/2015/02/person-icon.png',
            hasPlan: true,
            income: 420000,
            plan: {
                amount: 30000,
                duration: 3,
            },
            isNewMonth: true,
            isIncomeRegular: true,
            todayExpense: 0,
        }
    }

    getStatus() {

        const everydayEx = this.state.income/30
        const todayEx = this.state.todayExpense

        if(todayEx/everydayEx < 0.5) {
            return '1'
        } else if (todayEx/everydayEx < 0.8) {
            return '2'
        } else if(todayEx/everydayEx < 1) {
            return '3'
        } 

        return '4'
    }

    onSpend(amount) {

        const prevEx = this.state.todayExpense

        this.setState({
            todayExpense: prevEx + 5000
        })
    }

    render() {
        return <div>
            <NavBar />
            <UserJumbotron userData={this.state} statusColor = {this.getStatus()}/>
            <SpendingInput onSpend = {(amount) => this.onSpend(amount)} />
            // {/* <Button btnVal = "Spend" onClick = {() => this.onSpend()} /> */}
            {this.state.hasPlan ? <p> You have plan</p> : <p>You don't have a plan</p>}
        </div>
    }

}




// 7. Profile Page
// 	i. Name 
// 	ii. picture
// 	iii. status of plan (if exist)/none plan
// 	iv. expense input
// 	v. recieved confirming input (in case of first of months of regular income)
// 	vi. recieved income amount input (in case of non-regular income)
